export default function Hero() {
  return (
    <section
      id="hero"
      className="min-h-screen flex flex-col justify-center items-start space-y-6"
    >
      <p className="text-[#64ffda] text-lg">Hi, my name is</p>
      
      <h1 className="text-5xl md:text-6xl font-bold text-slate-100">
        Shaheer Dastgir
      </h1>
      
      <h2 className="text-2xl md:text-3xl font-semibold text-slate-400">
        IoT, Embedded Systems & Firmware Engineer
      </h2>

      <p className="max-w-2xl text-slate-300 text-justify">
        Designing and developing intelligent systems with{" "}
        <span className="text-[#64ffda]">ESP32</span>,{" "}
        <span className="text-[#64ffda]">Python</span>,{" "}
        <span className="text-[#64ffda]">C/C++</span>, {""} 
        <span className="text-[#64ffda]">FreeRTOS</span>,{" "}
        <span className="text-[#64ffda]">Linux device drivers</span>, and{" "}
        <span className="text-[#64ffda]">RISC-V research</span>. Passionate 
        about bridging hardware, software, and cloud to build smart, 
        connected solutions.
      </p>

      <div className="mt-6">
        <a
          href="#projects"
          className="inline-block px-6 py-3 border border-[#64ffda] text-[#64ffda] rounded hover:bg-[#64ffda]/10 transition"
        >
          Check out my work
        </a>
      </div>
    </section>
  );
}
